function changeImg(e,t){document.getElementById(e).addEventListener("change",e=>{const a=e.currentTarget.value.toLowerCase().replace("-","_"),n=document.getElementById(t).querySelector("img"),g=`${Joomla.getOptions("system.paths").rootFull}/media/mod_languages/images/${a}.gif`;a?(n.setAttribute("src",g),n.setAttribute("alt",a)):(n.removeAttribute("src"),n.setAttribute("alt",""))},!1)}document.addEventListener("DOMContentLoaded",()=>{changeImg("jform_image","flag"),changeImg("jform_fallback_lang","fallback_lang_flag")});